catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading) {
        return;
    }
    var searchResult = JSON.parse(g_form.getValue('search_result'));

    if (newValue === '') {
        for (key in searchResult[0]) {
            g_form.setValue(key, '');
        }
    } else {
        var filter = searchResult.filter(function(x) {
            return x.id == newValue;
        })[0];
        for (key in filter) {
            g_form.setValue(key, filter[key]);
            if (key === 'image_url') {
                // getBase64Image('https://www.google.de/images/srpr/logo11w.png');
                // toDataUrl('https://www.google.de/images/srpr/logo11w.png', function(myBase64) {
                //     alert(myBase64);
                // });
            }
        }
    }

    function toDataUrl(url, callback) {
        var xhr = new XMLHttpRequest();
        xhr.onload = function() {
            var reader = new FileReader();
            reader.onloadend = function() {
                callback(reader.result);
            };
            reader.readAsDataURL(xhr.response);
        };
        xhr.open('GET', url, true);
        xhr.setRequestHeader('Access-Control-Allow-Headers', '*');
        xhr.setRequestHeader('Access-Control-Allow-Origin', 'Origin, Content-Type, X-Auth-Token');
        xhr.responseType = 'blob';
        xhr.send();
    }

    function getBase64Image(url) {
        fetch(url, {
                mode: 'no-cors',
                method: 'GET'
            })
            .then(function(res) {
                alert(res.ok);
                res.blob().then(function(blob) {
                    var imageObjectURL = URL.createObjectURL(blob);
                    alert(imageObjectURL);
                    var reader = new FileReader();
                    reader.onload = function(e) {
                        alert('DataURL:', e.target.result);
                    };
                    reader.readAsDataURL(blob);
                });
            });
        // return dataURL.replace(/^data:image\/?[A-z]*;base64,/);
    }

}
