function onSubmit() {
    //Type appropriate comment here, and begin script below
    if (g_scratchpad.isFormValid) {
        return true;
    }
    var actionName = g_form.getActionName();
    var reg = /^\d+$/;
    var isbn13 = g_form.getValue('isbn_13');
    var isbn10 = g_form.getValue('isbn_10');
    var queryISBN = (isbn13 !== '' && reg.test(isbn13) && isbn13.length === 13) && (isbn10 !== '' &&
            reg.test(isbn10) && isbn10.length === 10) ? 'isbn10=' + isbn10 + '^ORisbn13=' + isbn13 :
        (isbn13 !== '' && reg.test(isbn13) && isbn13.length === 13) ? 'isbn13=' + isbn13 :
        (isbn10 !== '' && reg.test(isbn10) && isbn13.length === 10) ? 'isbn10=' + isbn10 : '';

    if (queryISBN != '') {
        var catalogGR = new GlideAjax('dataTable');
        catalogGR.addParam('sysparm_name', 'getData');
        catalogGR.addParam('sysparm_table', 'x_976190_library_catalog');
        catalogGR.addParam('sysparm_query', queryISBN);
        catalogGR.getXML(function(response) {
            var result = response.responseXML.documentElement.getAttribute("answer");
            if (result !== '0') {
                g_form.addErrorMessage('Catalog Item is already exist!!!');
            } else {
                // authorAndPublisher();
            }
        });

    } else {
        authorAndPublisher();
    }

    function authorAndPublisher() {
        var publisherGR = new GlideAjax('dataTable');
        publisherGR.addParam('sysparm_name', 'getData');
        publisherGR.addParam('sysparm_table', 'x_976190_library_publisher');
        publisherGR.addParam('sysparm_query', 'name=' + g_form.getValue('p_publisher'));
        publisherGR.getXML(function(response) {
            var result = response.responseXML.documentElement.getAttribute("answer");
            if (result !== 'null') {
                alert(publisherGR.getValue('sys_id'));
                // g_form.setValue('publisher', publisherGR.getValue('sys_id'));
            } else {
                publisherGR.addParam('sysparm_name', 'addData');
                publisherGR.addParam('sysparm_table', 'x_976190_library_publisher');
                publisherGR.addParam('sysparm_name', g_form.getValue('p_publisher'));
                publisherGR.getXML(function(response) {
					var result = response.responseXML.documentElement.getAttribute("answer");
					alert(result);
                // g_form.setValue('publisher', publisherGR.getValue('sys_id'));
				});
            }
        });
        // for (var i = 0; i <= copies.length; i++) {
        //     var copy = copies[i];
        //     if (copy) {
        //         queryISBN += 'sys_id=' + copies[i];
        //         if (i + 1 < copies.length) {
        //             queryISBN += '^OR';
        //         }
        //     }
        // }
        // isloaded();
        var authorRP = g_form.getValue('p_author').split(',');
        var authorGR = new GlideRecord('x_976190_library_author');
        for (var i = 0; i <= authorRP.length; i++) {
            if (authorGR[i] !== '') {
                authorGR.addQuery('name', authorGR[i]);
                authorGR.query();
                var authors = g_form.getValue('author') !== '' ? g_form.getValue('author') + ',' : '';
                if (authorGR.next()) {
                    authors += authorGR.getValue('sys_id');
                    g_form.setValue('author', authors);
                } else {
                    authorGR.name = authorGR[i];
                    authorGR.insert();
                    authors += authorGR.getValue('sys_id');
                    g_form.setValue('author', authors);
                }
            }
            if (i == authorRP.length) {
                // isloaded();
            }
        }
    }
    // function isloaded() {
    //     if (g_form.getValue('author') !== '' && g_form.getValue('publisher') !== '') {
    //         alert('author: ' + g_form.getValue('author') + ' publisher: ' + g_form.getValue('publisher'));
    //         // g_scratchpad.isFormValid = true;
    //         // g_form.submit(actionName);
    //     }
    // }
    return false;
}
