sn_doc_template_script.script:
(function runTemplateScript(target /*GlideRecord for target task*/ ) {
    var arr = [];
    var detailInfo = function(name, value) {
		return '<div class="col-xs-12"><strong>'+name+':</strong> '+value+'</div>';
    };
	var haveValue = function(input) {
        return input?.length>0 && input != '' && input != 'null' && input != null && input != undefined ? true : false;
    };
    if (haveValue(target.getValue('cmdb_ci'))) {
        arr.push(detailInfo('Configration Item',target.getValue('cmdb_ci')));
    }
	if (haveValue(target.getValue('business_service'))) {
        arr.push(detailInfo('Service',target.getValue('business_service')));
    }
	if (haveValue(target.getValue('service_offering'))) {
        arr.push(detailInfo('Service Offering',target.getValue('service_offering')));
    }
	if (haveValue(target.getValue('business_impact'))) {
        arr.push(detailInfo('Business Impact',target.getValue('business_impact')));
    }
	if (haveValue(target.getValue('problem_id'))) {
        arr.push(detailInfo('Problem',target.getValue('problem_id')));
    }
    return arr.join('<br />');

})(target);
