sys_script_include.script:
var HtmlToPDF = Class.create();
HtmlToPDF.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    convertToPDF: function() {
        var record = new GlideRecord('x_976190_library_pdf_template');
        record.addEncodedQuery(this.getParameter('sysparm_query'));
        record.query();
        if (record.next()) {
            var pdf, result;
            pdf = new sn_pdfgeneratorutils.PDFGenerationAPI;
            var html = record.getValue('html_template');
            var table = this.getParameter('sysparm_table');
            var sys_id = this.getParameter('sysparm_sys_id');
            var name = this.getParameter('sysparm_number');
            var attach = new GlideRecord('sys_attachment');
            attach.addQuery('table_sys_id', sys_id);
            attach.query();
            if (attach.next()) {
                //                 attach.deleteMultiple();
                result = pdf.convertToPDF(html, table, sys_id, name);
            } else {
                result = pdf.convertToPDF(html, table, sys_id, name);
            }
            return result;
        }
        return 'null';
    },
    type: 'HtmlToPDF'
});
