sys_script_include.script:
var dataTable = Class.create();
dataTable.prototype =  Object.extendsObject(global.AbstractAjaxProcessor, {
    initialize: function() {
    },
	getData:function(){
		var gr = new GlideRecord(this.getParameter('sysparm_table'));
		gr.addEncodedQuery(this.getParameter('sysparm_query'));
		gr.query();
		if(gr.hasNext()){
			return JSON.stringify(gr);
		}
		return JSON.stringify('0');
	},
	addData: function(){
		var gr = new GlideRecord(this.getParameter('sysparm_table'));
		gr.initialize();
		gr.name = this.getParameter('sysparm_name');
		gr.insert();
		return gr.getValue('sys_id');
	},
    type: 'dataTable'
});
